mapper.insert:
insert into activity(
project_id,questionnaire_id,status, del_flag, create_time, create_by_id, update_time, update_by_id
)
values(
#{projectId},#{questionnaireId},#{status}, #{delFlag}, #{createTime}, #{createById}, #{updateTime}, #{updateById}
)

mapper.delete:
update activity
set del_flag = 'Y',
    update_time = #{updateTime},
    update_by_id = #{updateById}
where id = #{id}

mapper.update:
update activity
title = #{title}, notice_content = #{noticeContent}, staff_count = #{staffCount}, volunteer_count = #{volunteerCount}, service_object_count = #{serviceObjectCount}, update_time = #{updateTime}, update_by_id = #{updateById}
where id = #{id}

update activity
set status = #{status},
update_time = #{updateTime},
update_by_id = #{updateById}

    ,reject_reason = #{rejectReason}

where id = #{id}
