mapper.select:
select *
from business
where order_no = #{orderNo}
  and apply_user_id = #{applyUserId}

select *
from business
where order_no = #{orderNo}
  and accept_expert_id = #{acceptExpertId}

select *
from business
where id = #{id} and accept_expert_id = #{userId}
   OR id = #{id} and apply_user_id = #{userId}

mapper.update:
update business
set status = #{newStatus},
    end_at = #{finishAt}
where id = #{id}
  and status = #{oldStatus}
