mapper.insert:
insert into project(
name,status, del_flag, create_time, create_by_id, update_time, update_by_id
)
values(
#{name},#{status}, #{delFlag}, #{createTime}, #{createById}, #{updateTime}, #{updateById}
)

mapper.delete:
update project
set del_flag = 'Y',
    update_time = #{updateTime},
    update_by_id = #{updateById}
where id = #{id}

mapper.update:
update project

    name = #{name},
    update_time = #{updateTime},
    update_by_id = #{updateById}

where id = #{id}

update project
set status = #{status},
update_time = #{updateTime},
update_by_id = #{updateById}

    ,reject_reason = #{rejectReason}

where id = #{id}
