mapper.update:
update questionnaire
set delete_flag = 1
where id = #{id}

update questionnaire
set status = 2 AND end_time = #{endTime}
where activity_id = #{activityId}

update questionnaire
set status = 1 AND start_time = #{startTime}
where activity_id = #{activityId}

mapper.select:
select *
from questionnaire
where status = #{status}
  AND delete_flag = 0
  AND uesr_id = #{uesrId}
