mapper.update:
update questionnaire
set delete_flag = 1
where questionnaire_id = #{questionnaireId}

update questionnaire
set status = 2 AND end_time = #{endTime}
where questionnaire_id = #{questionnaireId}

update questionnaire
set status = 1 AND start_time = #{startTime}
where questionnaire_id = #{questionnaireId}

update questionnaire
set fill_count = fill_count + 1
where questionnaire_id = #{questionnaireId}
