mapper.insert:
INSERT INTO user (
    name, phone, password, role, status, balance, create_at, last_login_time, updater_id,
                  update_at, delete_flag, location
) VALUES (
             #{name}, #{phone}, #{password}, #{role}, #{status}, #{balance}, #{createAt},
          #{lastLoginTime}, #{updaterId}, #{updateAt}, #{deleteFlag}, #{location}
         )

mapper.update:
UPDATE user
name = #{name},
description = #{description},
location = #{location},
updater_id = #{id}, update_at = #{updateAt}
WHERE id = #{id}
